-- 예약(booking) CREATE SQL
create table booking(
bookingId varchar(50) primary key,
userId varchar(20) not null,
carId int not null,
bookedDate varchar(20) not null,
startDate varchar(20) not null,
startTime varchar(20) not null,
endDate varchar(20) not null,
endTime varchar(20) not null,
carPrice int unsigned not null,
insurancePrice int unsigned not null,
totalPrice int unsigned not null,
paymentMethod varchar(45) not null
);
-- 예약(booking) INSERT SQL
INSERT INTO booking (
    bookingId,
    userId,
    carId,
    bookedDate,
    startDate,
    startTime,
    endDate,
    endTime,
    carPrice,
    insurancePrice,
    totalPrice,
    paymentMethod
)
SELECT
    bookingId,
    userId,
    carId,
    bookedDate,
    startDate,
    startTime,
    endDate,
    endTime,
    carPrice,
    insurancePrice,
    totalPrice,
    paymentMethod
FROM (
    SELECT
        CONCAT(
            UNIX_TIMESTAMP(b.start_dt) * 1000 + FLOOR(RAND()*1000),
            '_',
            base.userId
        ) AS bookingId,

        base.userId,
        base.carId,

        DATE_FORMAT(base.booked_dt, '%Y-%m-%d') AS bookedDate,
        DATE_FORMAT(b.start_dt, '%Y-%m-%d')     AS startDate,
        DATE_FORMAT(b.start_dt, '%H:%i')        AS startTime,
        DATE_FORMAT(b.end_dt, '%Y-%m-%d')       AS endDate,
        DATE_FORMAT(b.end_dt, '%H:%i')          AS endTime,

        base.rental_days * base.daily_price     AS carPrice,
        base.rental_days * 15000                AS insurancePrice,
        (base.rental_days * base.daily_price)
          + (base.rental_days * 15000)          AS totalPrice,

        ELT(FLOOR(RAND()*3)+1,
            '네이버페이',
            '무통장입금',
            '카카오페이'
        ) AS paymentMethod,

        ROW_NUMBER() OVER (
            PARTITION BY base.carId
            ORDER BY b.start_dt
        ) AS rn

    FROM (
        SELECT
            u.userId,
            c.carId,

            DATE_ADD('2026-01-01', INTERVAL FLOOR(RAND()*58) DAY) AS booked_dt,

            DATE_ADD(
                DATE_ADD(
                    '2026-01-02 08:00:00',
                    INTERVAL FLOOR(RAND()*100) DAY
                ),
                INTERVAL FLOOR(RAND()*10) HOUR
            ) AS start_dt,

            FLOOR(RAND()*7) + 1 AS rental_days,

            CASE c.carSize
                WHEN '경소형' THEN 50000
                WHEN '중형'   THEN 80000
                WHEN '대형'   THEN 120000
                ELSE 70000
            END AS daily_price

        FROM user u
        JOIN car c
            ON (
                (CAST(u.license AS UNSIGNED) = 1 AND c.licenseType IN (1,2))
                OR
                (CAST(u.license AS UNSIGNED) = 2 AND c.licenseType = 2)
            )
        ORDER BY RAND()
        LIMIT 400
    ) base
    CROSS JOIN LATERAL (
        SELECT
            base.start_dt AS start_dt,
            DATE_ADD(base.start_dt, INTERVAL base.rental_days DAY) AS end_dt
    ) b
    WHERE base.booked_dt < b.start_dt
      AND NOT EXISTS (
          SELECT 1
          FROM booking bk
          WHERE bk.carId = base.carId
            AND STR_TO_DATE(CONCAT(bk.startDate,' ',bk.startTime), '%Y-%m-%d %H:%i') < b.end_dt
            AND STR_TO_DATE(CONCAT(bk.endDate,' ',bk.endTime), '%Y-%m-%d %H:%i') > b.start_dt
      )
) t
WHERE t.rn = 1
LIMIT 250;

